    return gulp
        .src('./public/css/styles.css') // Creamos la ruta de los archivos
        .pipe(clean({
            content: ['./public/*.html']
        }))
        .pipe(gulp.dest('./public/css')) // Le decimos la ruta del los archivos ya listos para subir al servidor
})

